import React from 'react';
import PropTypes from 'prop-types';
import {connect} from 'react-redux';
const Alert = ({alerts}) => (
    alerts.map(alert => (
        <div key={alert.id}>
            {alert.msg}
        </div>
    ))
)


Alert.propTypes = {
    alerts:PropTypes.array.isRequired
}
const maptoState = state => ({ 
    alerts:state.alert
});
export default connect(maptoState)(Alert);
