import React,{Fragment} from 'react';

const ProfileAbout = ({profile:{bio,journals,user:{name}}}) => {
    return (
        <div class="profile-about bg-light p-2">
            {bio && (<Fragment>
                <h2 class="text-primary">{name.trim().split(' ')[0]} Bio</h2>
                <p>
                {bio}
                </p>
            </Fragment>)}
            <div class="line"></div>
            {journals.length!==0 && (<Fragment>
                <h2 class="text-primary">Papers/Journals published</h2>
                <div class="skills">
                {journals.map((journal,index) => (
                    <div key={index} class="p-1">{journal && <i class="fa fa-book"></i>}{'       '}{journal}</div>    
                ))}
                </div>

                </Fragment>)}
        </div>
    )
}

export default ProfileAbout;
