import {
    GET_POSTS,
    POST_ERROR,
    UPDATE_STARS
}from '../actions/types';
const initialstate = {
    posts:[],
    post:null,
    loading:true,
    error:{}
}
export default function(state=initialstate,action){
    const { type,payload } = action;
    switch(type){
        case GET_POSTS:
            return{
                ...state,
                posts:payload,
                loading:false
            }
        case POST_ERROR:
            return{
                ...state,
                error:payload,
                loading:false
            }
        case UPDATE_STARS:
            return{
                ...state,
                posts:state.posts.map(post => post._id === payload.id ? ({...post,stars:payload.stars}):post)
            }      
        default:
            return state      
    }
}
