import React,{useEffect, Fragment} from 'react';
import {connect} from 'react-redux';
import Spinner from '../Layout/Spinner';
import {getPost} from '../../actions/post';
import PropTypes from 'prop-types';
import {Link} from 'react-router-dom';
import Moment from 'react-moment';
import Button from '../Button';
import DelButton from '../DeleteButton';
import {starPost,unstarPost} from '../../actions/post';

const Post = ({post:{post,loading},getPost,match}) => {
    useEffect(()=>{
        window.scrollTo(0, 0);
        getPost(match.params.id);
    },[getPost]);
    const starupdate = (id) => {
        if(!auth.user){
            return null;
        }
        if(!auth.loading && post.stars.filter(el => el.user===auth.user._id).length===0){
            starPost(id);
        }
        else if(!auth.loading){
            unstarPost(id);
        }
    }
    let displaystar = (<i className='far fa-star'></i>)
    if(auth && auth.isAuthenticated && !auth.loading){
        if(stars.filter(el => el.user===auth.user._id).length!==0){
            displaystar = (<i className="fas fa-star"></i>)
        }
    }


    return loading || post==null ? <Spinner/> :(<Fragment>
        <div className="post bg-dark p-1 my-1">
            <div>
                <Link to={`/profile/${post.user}`}>
                    <img
                        className="round-img"
                        src={post.avatar}
                        alt=""
                    />
                    <h4>{post.name}</h4>
                </Link>
            </div>
            <div>
                <h3 style={{color:'#90a39b'}}>{post.title}</h3>
                <p className="my-1">
                    {text.slice(0,150)+' ...'}
                </p>
                <p className="post-date">
                    Posted on <Moment format='DD/MM/YYYY'>{date}</Moment>
                </p>
                <span onClick={e => starupdate(post._id)}>
                    <Button>
                        {displaystar}{' '}
                        {post.stars.length>0 && <span>{post.stars.length}</span>}
                    </Button>
                </span>

            </div>
        </div>    
    </Fragment>)

}

Post.propTypes = {
    getPost:PropTypes.func.isRequired,
    post:PropTypes.object.isRequired,
}
const mapstatetoprops = state => ({
    post:state.post,
    auth:state.auth
})

export default connect(mapstatetoprops,{getPost,starPost,unstarPost})(Post);
