import React,{Fragment} from 'react';
import PropTypes from 'prop-types';
import {connect} from 'react-redux';
import {Link} from 'react-router-dom';
import Moment from 'react-moment';
import Button from '../Button';
import {starPost,unstarPost} from '../../actions/post';
const PostsItem = ({auth,post:{_id,text,name,title,avatar,stars,date,comments,user},starPost,unstarPost}) => {
    const starupdate = (id) => {
        if(!auth.loading && stars.filter(el => el.user===auth.user._id).length===0){
            starPost(id);
        }
        else if(!auth.loading){
            unstarPost(id);
        }
    }
    return _id && ( 
        <div className="posts">
            <div className="post bg-dark p-1 my-1">
                <div>
                    <Link to="profile.html">
                        <img
                            className="round-img"
                            src={avatar}
                            alt=""
                        />
                        <h4>{name}</h4>
                    </Link>
                </div>
                <div>
                    <h3 style={{color:'#90a39b'}}>{title}</h3>
                    <p className="my-1">
                        {text}
                    </p>
                    <p className="post-date">
                        Posted on <Moment format='DD/MM/YYYY'>{date}</Moment>
                    </p>
                    <Button className="btn btn-light">
                        <span onClick={e => starupdate(_id)}>
                            {!auth.loading && stars.filter(el => el.user===auth.user._id).length!==0 ? (<i className="fas fa-star"></i>):(<i className='far fa-star'></i>)}{' '}
                            {stars.length>0 && <span>{stars.length}</span>}
                        </span>
                    </Button>                   
                    {!auth.loading && user === auth.user._id &&(
                    <Button      
                    type="secondary"
                    >
                    <i className="fas fa-times"></i>
                    </Button>
                    )}
                </div>
            </div>    
        </div>
        )

}

PostsItem.propTypes = {
    post:PropTypes.object.isRequired,
    auth:PropTypes.object.isRequired,
    starPost:PropTypes.func.isRequired,
    unstarPost:PropTypes.func.isRequired,
}
const mapStatetoProps = state => ({
    auth:state.auth
})
export default connect(mapStatetoProps,{starPost,unstarPost})(PostsItem);
