import React,{Fragment} from 'react';
import PropTypes from 'prop-types';
import {connect} from 'react-redux';
import {Link} from 'react-router-dom';
import Moment from 'react-moment';
import Button from '../Button';

const PostsItem = ({auth,post:{_id,text,name,title,avatar,stars,date,comments,user}}) => {
    return (
        <div class="posts">
            <div class="post bg-dark p-1 my-1">
                <div>
                    <Link to="profile.html">
                        <img
                            class="round-img"
                            src={avatar}
                            alt=""
                        />
                        <h4>{name}</h4>
                    </Link>
                </div>
                <div>
                    <h3 style={{color:'#90a39b'}}>{title}</h3>
                    <p class="my-1">
                        {text}
                    </p>
                    <p class="post-date">
                        Posted on <Moment format='DD/MM/YYYY'>{date}</Moment>
                    </p>
                    <Button class="btn btn-light">
                        <span>
                            <i class="far fa-star"></i>{' '}
                            {stars.length>0 && <span>{stars.length}</span>}
                        </span>
                    </Button>
                    <Link to={`/post/${_id}`}><Button type='primary'>
                    View Full Post and Discussions
                    </Button>
                    </Link>
                    {!auth.loading && user === auth.user._id &&(
                    <Button      
                    type="secondary"
                    >
                    <i class="fas fa-times"></i>
                    </Button>
                    )}
                </div>
            </div>    
        </div>

    )
}

PostsItem.propTypes = {
    post:PropTypes.object.isRequired,
    auth:PropTypes.object.isRequired,
}
const mapStatetoProps = state => ({
    auth:state.auth
})
export default connect(mapStatetoProps,{})(PostsItem);
